\left[\begin{array}{cccc}
	x^{(1)}_{0} &amp;amp; x^{(2)}_{0} &amp;amp; \cdots &amp;amp; x^{(m)}_{0} \\
	x^{(1)}_{1} &amp;amp; x^{(2)}_{1} &amp;amp; \cdots &amp;amp; x^{(m)}_{1} \\
	\vdots &amp;amp; \vdots &amp;amp; \ddots &amp;amp; \vdots \\
	x^{(1)}_{n} &amp;amp; x^{(2)}_{n} &amp;amp; \cdots &amp;amp; x^{(m)}_{n} \\
\end{array}\right]
\left[\begin{array}{c}
	\boldsymbol{\theta}^{T}\boldsymbol{\hat{x}^{(1)}} - y^{(1)} \\
	\boldsymbol{\theta}^{T}\boldsymbol{\hat{x}^{(2)}} - y^{(2)} \\
	\vdots \\
	\boldsymbol{\theta}^{T}\boldsymbol{\hat{x}^{(m)}} - y^{(m)} \\
\end{array}\right] = 2
\mathop{
\left[\begin{array}{cccc}
	x^{(1)}_{0} &amp;amp; x^{(2)}_{0} &amp;amp; \cdots &amp;amp; x^{(m)}_{0} \\
	x^{(1)}_{1} &amp;amp; x^{(2)}_{1} &amp;amp; \cdots &amp;amp; x^{(m)}_{1} \\
	\vdots &amp;amp; \vdots &amp;amp; \ddots &amp;amp; \vdots \\
	x^{(1)}_{n} &amp;amp; x^{(2)}_{n} &amp;amp; \cdots &amp;amp; x^{(m)}_{n} \\
\end{array}\right]
}\limits_{\boldsymbol{X^{T}}}
\left(
	\mathop{
	\left[\begin{array}{cccc}
		\boldsymbol{\hat{x}^{(1)}} \\
        \boldsymbol{\hat{x}^{(2)}} \\
        \vdots \\
        \boldsymbol{\hat{x}^{(m)}}
	\end{array}\right]
	}\limits_{\boldsymbol{X}}
	\boldsymbol{\theta}
    - 
	\left[\begin{array}{cccc}
		y^{(1)} \\
        y^{(2)} \\
        \vdots \\
        y^{(m)}
	\end{array}\right]
\right) = 
2 \boldsymbol{X^{T}}(\boldsymbol{X}\boldsymbol{\theta} - \boldsymbol{y}) = \boldsymbol{0} \tag{5}
